mapper.insert:
insert into cards (
card_number, card_name, account_number, type, emp_id, use_detail, use,
remarks
) values(
#{card_number}, #{card_name}, #{account_number},
#{type}, #{emp_id}, #{use_detail}, #{use}, #{remarks}
)

mapper.update:
UPDATE cards SET card_number = #{card_number},
card_name = #{card_name}, account_number = #{account_number},
type = #{type}, emp_id = #{emp_id}, use_detail = #{use_detail}, use = #{use},
remarks = #{remarks}
WHERE card_number = #{card_number}
