mapper.insert:
insert into funds_plan (
typelist, funds_id, repeat_unit, start_date, period, dept_id, proj_id,
account_number, diagnosis_number, customer_id, sums, remarks
) values(
#{typelist}, #{funds_id}, #{repeat_unit},
#{start_date}, #{period}, #{dept_id}, #{proj_id}, #{account_number}, #{diagnosis_number}, #{customer_id}, #{sums}, #{remarks}
)

mapper.update:
UPDATE funds_plan SET typelist = #{typelist},
funds_id = #{funds_id}, repeat_unit = #{repeat_unit},
start_date =#{start_date}, period = #{period}, account_number = #{account_number}, diagnosis_number = #{diagnosis_number},
customer_id = #{customer_id}, sums = #{sums}, remarks = #{remarks}
WHERE funds_id = #{funds_id}
